import {useState, useEffect} from 'react';
import BlogList from './BlogList';


const Home = () => {
    const [blogs,setBlogs]= useState(null);
    
    const handleDeleteClick = (id)=>{
        const newBlogs= blogs.filter(blog=> blog.id!==id);
        setBlogs(newBlogs);
    }

    useEffect(()=>{
    fetch('http://localhost:8000/blogs')
    .then(res=>{
        return res.json();
    })
    .then(data=>{
        console.log(data);
        setBlogs(data);
    })
    },[]);

    return ( 
        <div className="home">
            {blogs &&<BlogList blogs={blogs} title="All Blogs" handleDelete={handleDeleteClick}/>}
        </div>
     );
}
 
export default Home;
